package com.icss.hit.struts.action;

import java.net.URLEncoder;
import java.util.List;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import com.icss.hit.bean.ConfigWorkBean;
import com.icss.hit.bean.interfaces.ConfigWork;
import com.icss.hit.component.PageBean;
import com.icss.hit.hibernate.vo.ScheduleConfig;

/** 
 * 得到当前用户所有已授权代办人员的列表
 * Creation date: 08-03-2009
 * @author 朱金彪
 * XDoclet definition:
 * @struts.action validate="true"
 * @struts.action-forward name="authorizedUser.succeed" path="/work/authorizedUser.jsp"
 */
public class AuthorizedUserAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		// 当前登陆用户ID
		long id=-1;
		if( request.getSession().getAttribute("UserId") != null ){
			id = Long.parseLong(request.getSession().getAttribute("UserId").toString());
		}else{
			return mapping.findForward("NullLogin");
		}
		// 查询页码
		int pageNo = 1;
		try{
			pageNo = Integer.parseInt(request.getParameter("page"));
		}
		catch(Exception e){
			pageNo =1;
		}
		
		// 所有人员的数量
		int count = 0;
		// 分页的数量
		int pageCount = 0;
		List<ScheduleConfig> list = null;
		if( id != -1){
			ConfigWork work = new ConfigWorkBean();
			
			// 得到总数和分页
			count = work.getAllConfigWorkUserCount(id);
			pageCount = work.getPageCount(count, ConfigWorkBean.PAGE_SIZE);
			
			// 设置页面的范围
			pageNo = pageNo < 1? 1:pageNo;
			pageNo = pageNo > pageCount? pageCount: pageNo;
			
			// 得到所有授权人列表
			list = work.getAllConfigWorkUser(id, pageNo);
			request.setAttribute("authorizedUserList", list);
						
			// 生成前面的页面显示
			PageBean pagebean = new PageBean();
			pagebean.addParam("userId=" + id );
			pagebean.setLink("authorizedUser.do");
			pagebean.setTotal(pageCount);
			pagebean.setThispage(pageNo);
			request.setAttribute("pageNo",pageNo);
			request.setAttribute("pageString", pagebean.getPageString());
		}
		return mapping.findForward("authorizedUser.succeed");
	}
}